:root {
    --qud-color-y-dark: #b1c9c3;
    --qud-bg-color: rgb(6,28,23);
    --qud-viridian: rgba(8,51,50,1);
    --stripe1: var(--qud-viridian);
    --stripe2: #082F2D;
    --qud-font-terminal: 'Source Code Pro', 'Consolas', 'Courier New', 'Courier', 'Lucida Console';
}

#content {
    font-family: var(--qud-font-terminal);
    color: var(--qud-color-y-dark);
    background: green;
}

.messages ul li.chat-message .message-self,
.messages ul li.chat-message .message {
    background: -moz-repeating-linear-gradient(var(--stripe1) 0px, var(--stripe1) 2px, var(--stripe2) 2px, var(--stripe2) 4px);
    background: -webkit-repeating-linear-gradient(var(--stripe1) 0px, var(--stripe1) 2px, var(--stripe2) 2px, var(--stripe2) 4px);
    background: repeating-linear-gradient(var(--stripe1) 0px, var(--stripe1) 2px, var(--stripe2) 2px, var(--stripe2) 4px);
    background-size: 100% 4px;
    flex: 0 1 auto;
    border-style: solid;
    border-color: var(--qud-color-y-dark);
    border-width: 4px;
    border-radius: 0;
}
.messages ul li.chat-message .message-bot {
    visibility:hidden;
    overflow:hidden;
    max-height:1px;
    font-size:1px;
    padding:0;
    white-space: nowrap;
}
div .message-wrapper {
    justify-items: flex-end;
    justify-content: flex-end;
    max-width: 30em; /* edit this number until you're happy */
}

.owner, .owner-self {
    visibility:hidden;
    overflow:hidden;
    white-space: nowrap;
}

 .avatar-wrapper, .avatar-wrapper-self  {
    visibility:hidden;
    flex-shrink:0;
    height:0px;
}
